import React, {useEffect, useState} from "react";
import { useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";


function EditContact() {
  const { id } = useParams();
  const contacts = useSelector(state => state);
  const currentContact = contacts.find(contact => contact.id === parseInt(id));
  useEffect
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [number, setNumber] = useState("");

  return (
    <div className="container">
      {currentContact ? (
        <>
      <h1 className="display-3 my-5 text-center">Edit Student {id}</h1>
      <div className="row">
        <div className="col-md-6 shadow p-5 mx-auto">
          <form>
            <div className="form-group my-2">
              <input type="text" placeholder="Name" className="form-control" />
            </div>
            <div className="form-group my-2">
              <input
                type="email"
                placeholder="Email"
                className="form-control"
              />
            </div>
            <div className="form-group my-2">
              <input
                type="number"
                placeholder="Phone number"
                className="form-control"
              />
            </div>
            <div className="form-group my-2">
              <input
                type="submit"
                value="Update Student"
                className="btn btn-dark"
              />
              <Link to="/" className="btn btn-danger ml-6">
                Cancel
              </Link>
            </div>
          </form>
        </div>
          </div>
          </>
      ) : (
          <h1 className="display-3 my-5 text-center">Student contact with id: {id} does not exist</h1>
        )
      }
    </div>
  );
}

export default EditContact;
